'use client';

import * as React from 'react';
import { Autocomplete as BaseAutocomplete } from '@base-ui-components/react/autocomplete';
import { 
	Autocomplete,
  AutocompleteInput,
  AutocompletePortal,
  AutocompletePositioner,
  AutocompletePopup,
  AutocompleteStatus,
  AutocompleteList,
  AutocompleteItem,
	AutocompleteControl,
	AutocompleteClear
} from '@/components/ui/base-autocomplete';
import { Label } from '@/components/ui/base-label';
import { LoaderCircle } from 'lucide-react';
import { Avatar, AvatarImage, AvatarFallback } from '@/components/ui/base-avatar';

export default function AsyncSearchExample() {
  const [searchValue, setSearchValue] = React.useState('');
  const [isLoading, setIsLoading] = React.useState(false);
  const [searchResults, setSearchResults] = React.useState<Developer[]>([]);
  const [error, setError] = React.useState<string | null>(null);

  const { contains } = BaseAutocomplete.useFilter({ sensitivity: 'base' });

  React.useEffect(() => {
    if (!searchValue) {
      setSearchResults([]);
      setIsLoading(false);
      return undefined;
    }

    setIsLoading(true);
    setError(null);

    let ignore = false;

    async function fetchDevelopers() {
      try {
        const results = await searchDevelopers(searchValue, contains);
        if (!ignore) {
          setSearchResults(results);
        }
      } catch {
        if (!ignore) {
          setError('Failed to fetch developers. Please try again.');
          setSearchResults([]);
        }
      } finally {
        if (!ignore) {
          setIsLoading(false);
        }
      }
    }

    const timeoutId = setTimeout(fetchDevelopers, 300);

    return () => {
      clearTimeout(timeoutId);
      ignore = true;
    };
  }, [searchValue, contains]);

  let status: React.ReactNode = '';

  if (isLoading) {
    status = (
      <div className="flex items-center gap-2">
        <LoaderCircle className="size-4 animate-spin" aria-hidden />
        Searching developers...
      </div>
    );
  } else if (error) {
    status = error;
  } else if (searchResults.length === 0 && searchValue) {
    status = `No developers found for "${searchValue}"`;
  } else if (searchResults.length > 0) {
    status = `${searchResults.length} developer${searchResults.length === 1 ? '' : 's'} found`;
  } else if (!searchValue) {
    status = 'Start typing to search developers...';
  }

  const shouldRenderPopup = searchValue !== '';

  return (
    <div className="w-full max-w-md space-y-4">
			<Autocomplete
        items={searchResults}
        value={searchValue}
        onValueChange={setSearchValue}
        itemToStringValue={(item: unknown) => (item as Developer).name}
        filter={null}
      >
        <Label className="flex flex-col gap-2">
          <span className="text-sm font-medium">Search developers</span>
					<AutocompleteControl>
						<AutocompleteInput
							placeholder="e.g. John Smith, React, San Francisco"
						/>
						{searchValue && (
							<AutocompleteClear/>
						)}
					</AutocompleteControl>
        </Label>      
        {shouldRenderPopup && (
          <AutocompletePortal>
            <AutocompletePositioner sideOffset={4} align="start">
              <AutocompletePopup>
								<AutocompleteStatus>
									{status}
								</AutocompleteStatus>
                <AutocompleteList>
                  {(developer: Developer) => (
                    <AutocompleteItem
                      key={developer.id}
                      value={developer}
                      className="rounded-lg"
                    >
                      <div className="flex items-center gap-2.5">
												<Avatar className="size-9">
													<AvatarImage
														src={developer.avatar}
														alt={developer.name}
													/>
													<AvatarFallback>
														{developer.name.split(' ').map(n => n[0]).join('')}
													</AvatarFallback>
												</Avatar>	
                        <div className="flex-1 min-w-0">
                          <div className="font-medium truncate">{developer.name}</div>
                          <div className="text-sm text-muted-foreground truncate">
                            {developer.role} â€¢ {developer.location}
                          </div>
                        </div>
                      </div>
                    </AutocompleteItem>
                  )}
                </AutocompleteList>
              </AutocompletePopup>
            </AutocompletePositioner>
          </AutocompletePortal>
        )}
      </Autocomplete>
    </div>
  );
}

async function searchDevelopers(
  query: string,
  filter: (item: string, query: string) => boolean,
): Promise<Developer[]> {
  // Simulate network delay
  await new Promise((resolve) => {
    setTimeout(resolve, Math.random() * 800 + 200);
  });

  // Simulate occasional network errors (2% chance)
  if (Math.random() < 0.02 || query === 'error') {
    throw new Error('Network error');
  }

  return topDevelopers.filter(
    (developer) =>
      filter(developer.name, query) ||
      filter(developer.role, query) ||
      filter(developer.location, query) ||
      developer.skills.some(skill => filter(skill, query))
  );
}

