  AutocompletePortal,
  AutocompletePositioner,
  AutocompletePopup,
  AutocompleteList,
  AutocompleteItem,
  AutocompleteEmpty,
  AutocompleteClear,
  AutocompleteControl,
} from '@/components/ui/base-autocomplete';
import { Badge } from '@/components/ui/base-badge';
import { Label } from '@/components/ui/base-label';

export default function AutocompleteDemo() {
  const [value, setValue] = useState<string>('');

  const filteredItems = techStacks.filter((item) =>
    item.name.toLowerCase().includes(value.toLowerCase()) ||
    item.category.toLowerCase().includes(value.toLowerCase()) ||
    item.description.toLowerCase().includes(value.toLowerCase())
  );

  return (
    <div className="w-full max-w-sm">
      <Autocomplete
        value={value}
        onValueChange={setValue}
        items={filteredItems}
        itemToStringValue={(item: unknown) => (item as TechStack).name}
      >
        <Label className="flex flex-col gap-2.5">
					Search Tech Stack
					<AutocompleteControl>
						<AutocompleteInput
							placeholder="Type to search frameworks, libraries..."
						/>
						{value && (
							<AutocompleteClear/>
						)}
					</AutocompleteControl>
				</Label>
        <AutocompletePortal>
          <AutocompletePositioner sideOffset={4}>
            <AutocompletePopup>
              <AutocompleteList>
                {filteredItems.map((item) => (
                  <AutocompleteItem key={item.id} value={item} className="group/autocomplete-item">
                    <div className="flex flex-col items-stretch grow">
                      <div className="flex items-center gap-2">
                        <span className="font-medium">{item.name}</span>
												<Badge variant="outline" size="sm" className="group-data-[highlighted]/autocomplete-item:bg-background group-data-[highlighted]/autocomplete-item:border-intput">{item.category}</Badge>
                      </div>
                      <span className="text-xs text-muted-foreground roup-data-[highlighted]/autocomplete-item:text-secondary-foreground">{item.description}</span>
